function runTabsFilter2(){document.querySelectorAll(".tab-wrapper").forEach(e=>{const s=e.querySelectorAll(".tab-filters"),t=e.querySelectorAll(".tabs-content");for(let e=0;e<s.length;e++)s[e].onclick=function(){s.forEach(e=>{e.classList.remove("active")}),t.forEach(e=>{e.classList.remove("show"),e.classList.add("hide")}),s[e].classList.add("active"),t[e].classList.remove("hide"),t[e].classList.add("show")}})}runTabsFilter2();