document.addEventListener("DOMContentLoaded",()=>{window.onscroll=function(){const e=document.documentElement.scrollTop;window.innerWidth>1200&&(e>70?document.querySelector(".header").classList.add("sticky"):document.querySelector(".header").classList.remove("sticky"))},document.querySelector(".toggle__btn").addEventListener("click",()=>{document.querySelector(".navigation").classList.toggle("show"),document.querySelector(".toggle__btn").classList.toggle("active"),document.querySelector("body").classList.toggle("lock")}),document.querySelectorAll(".navigation__link").forEach(e=>{e.addEventListener("click",()=>{document.querySelector(".navigation").classList.remove("show"),document.querySelector(".toggle__btn").classList.toggle("active"),document.querySelector("body").classList.remove("lock")})})});