document.addEventListener("DOMContentLoaded",()=>{document.querySelector(".toggle__btn").addEventListener("click",()=>{document.querySelector(".navigation").classList.toggle("show"),document.querySelector(".toggle__btn").classList.toggle("active"),document.querySelector("body").classList.toggle("lock")}),document.querySelectorAll(".navigation__link").forEach(e=>{e.addEventListener("click",()=>{document.querySelector(".navigation").classList.remove("show"),document.querySelector(".toggle__btn").classList.toggle("active"),document.querySelector("body").classList.remove("lock")})})});